package firstjavaclass;

public class UnaryDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int x = 20;
		int result;
		
		result = +x;
		System.out.println("Result: "+result);
		
		result = -x;
		System.out.println("Result: "+result);
		
		result = ++x;
		System.out.println("Result: "+result);
		
		result = --x;
		System.out.println("Result: "+result);

	}

}
